mapper.select:
select 
  user_id,
  user_name,
  password,
  salt,
  phone,
  email,
  fullname,
  nickname,
  en_name,
  initial,
  sex,
  status,
  is_online,
  login_time,
  creator,
  ctime,
  modifier,
  mtime,
  user_type,
  avatar,
  merchant_id
from admin_user


     user_id=#{userId}


     and user_name=#{userName}


     and password=#{password}


     and salt=#{salt}


     and phone=#{phone}


     and email=#{email}


     and fullname=#{fullname}


     and nickname=#{nickname}


     and en_name=#{enName}


     and initial=#{initial}


     and sex=#{sex}


     and status=#{status}


     and is_online=#{isOnline}


     and login_time=#{loginTime}


     and creator=#{creator}


     and ctime=#{ctime}


     and modifier=#{modifier}


     and mtime=#{mtime}


     and user_type=#{userType}


     and avatar=#{avatar}

