  mtime,
  user_type,
  merchant_id
from admin_user_role


     id=#{id}


     and user_id=#{userId}


     and role_id=#{roleId}


     and creator=#{creator}


     and ctime=#{ctime}


     and modifier=#{modifier}


     and mtime=#{mtime}


     and user_type=#{userType}


     and merchant_id=#{merchantId}

