mapper.select:
select
alarm_id,
category_id,
device_id,
store_id,
strore_area_id,
device_name,
sort_order,
alarm_type,
level,
status,
remark,
creator,
ctime,
modifier,
mtime
from alarm_manage


		alarm_id=#{alarmId}


		and category_id=#{categoryId}


		and device_id=#{deviceId}


		and store_id=#{storeId}


		and strore_area_id=#{stroreAreaId}


		and device_name=#{deviceName}


		and sort_order=#{sortOrder}


		and alarm_type=#{alarmType}


		and level=#{level}


		and status=#{status}


		and remark=#{remark}


		and creator=#{creator}


		and ctime=#{ctime}


		and modifier=#{modifier}


		and mtime=#{mtime}

