mapper.sql:
		from alarm_manage


				alarm_id=#{alarmId}


				and category_id=#{categoryId}


				and device_id=#{deviceId}


				and store_id=#{storeId}


				and strore_area_id=#{stroreAreaId}


				and device_name=#{deviceName}


				and sort_order=#{sortOrder}


				and alarm_type=#{alarmType}


				and level=#{level}


				and status=#{status}


				and remark=#{remark}


				and creator=#{creator}



            and date_format(ctime,'%Y-%m-%d') = #{ctime}



				and modifier=#{modifier}


				and mtime=#{mtime}

mapper.select:
SELECT alarm_type alarmType1,( SELECT alarm_type_name FROM alarm_type WHERE alarm_type = alarm_type_id ) alarmType , COUNT(alarm_type) count FROM alarm_manage WHERE 1 = 1
AND date_format(ctime, '%Y-%m-%d') = date_format(now(), '%Y-%m-%d')
AND date_format(ctime, '%Y-%m') = date_format(now(), '%Y-%m')
AND date_format(ctime, '%Y') = date_format(now(), '%Y')
AND date_format(ctime, '%Y-%m-%d') >= #{starTime}
AND date_format(ctime,'%Y-%m-%d') <= #{endTime}
AND store_id = #{storeId}
AND status = #{status}
AND date_format(ctime, '%Y') = #{date}
AND date_format(ctime, '%Y-%m') = #{date}
AND date_format(ctime, '%Y-%m-%d') = #{date}
GROUP BY alarm_type

SELECT `status`, ( CASE `status` WHEN 1 THEN '已处理' WHEN 0 THEN '未处理' ELSE '未知' END ) name, COUNT(*) count FROM alarm_manage WHERE
date_format(ctime, '%Y') = #{date}
date_format(ctime, '%Y-%m') = #{date}
date_format(ctime, '%Y-%m-%d') = #{date}
GROUP BY `status`
