mapper.select:
select 
  permission_code_id,
  code,
  name,
  sort_order,
  status,
  description
from admin_permission_code


     permission_code_id=#{permissionCodeId}


     and code=#{code}


     and name=#{name}


     and sort_order=#{sortOrder}


     and status=#{status}


     and description=#{description}


		order by sort_order

