mapper.select:
select 
  permission_id,
  name,
  code,
  menu_id,
  url,
  sort_order,
  status,
  user_type
from admin_permission


     permission_id=#{permissionId}


     and name=#{name}


     and code=#{code}


     and menu_id=#{menuId}


     and url=#{url}


     and sort_order=#{sortOrder}


     and status=#{status}


     and user_type=#{userType}

