mapper.sql:
   	from admin_permission p 
		LEFT JOIN admin_menu sm ON p.menu_id = sm.menu_id 
		LEFT JOIN admin_menu fm ON sm.pid = fm.menu_id
		LEFT JOIN admin_permission_code c ON p.code = c.code


   	 	and sm.menu_id = #{secondMenuId}


