mapper.sql:
from perception_hcs


		hcs_id=#{hcsId}


		and device_id=#{deviceId}


		and temperature=#{temperature}


		and humidity=#{humidity}


		and air_status=#{airStatus}


		and drying_status=#{dryingStatus}


		and wetting_status=#{wettingStatus}


		and ventilation_status=#{ventilationStatus}


		and cleansing_status=#{cleansingStatus}


		and warning_status=#{warningStatus}


		and collect_date=#{collectDate}


		and remark=#{remark}

mapper.select:
SELECT * FROM perception_hcs WHERE 1=1
AND device_id=#{deviceId}
ORDER BY collect_date DESC LIMIT 0,1

SELECT b.store_id storeId,(SELECT wms_store.`name` FROM wms_store WHERE wms_store.store_id=b.store_id ) storeName, TRUNCATE(AVG(b.temperature),2) avgtem, TRUNCATE(AVG(b.humidity),2) avghum,b.* FROM ( SELECT * FROM ( SELECT device_manage.store_id, perception_hcs.* FROM perception_hcs, device_manage WHERE 1=1
AND device_manage.store_id =#{storeId}
AND perception_hcs.device_id = device_manage.device_id ORDER BY perception_hcs.collect_date DESC ) AS a GROUP BY device_id ORDER BY collect_date DESC ) AS b GROUP BY b.store_id

SELECT a.store_id storeId, a. NAME name, a.time ,DATE_FORMAT(a.time1 ,
'%H'
'%d'
'%m'
'%H'
) date, TRUNCATE(AVG(a.hum),2) hums, TRUNCATE(AVG(a.tem),2) tems FROM ( SELECT wms_store.store_id , wms_store.`name` , DATE_FORMAT( perception_hcs.collect_date ,
'%Y-%m-%d %H'
'%Y-%m-%d'
'%Y-%m'
'%Y-%m-%d %H'
) time ,DATE_FORMAT( perception_hcs.collect_date , '%Y-%m-%d %h' ) time1 , AVG(humidity) hum , AVG(temperature) tem FROM perception_hcs , device_manage , wms_store WHERE perception_hcs.device_id = device_manage.device_id AND device_manage.store_id = wms_store.store_id
AND device_manage.store_id = #{storeId}
AND perception_hcs.collect_date >=#{stime}
AND perception_hcs.collect_date <=#{etime}
GROUP BY collect_date ) AS a GROUP BY a.store_id , a.time ORDER BY a.store_id,a.time

SELECT MAX(p.temperature) temMax, MIN(p.temperature) temMin, MAX(p.humidity) humMax, MIN(p.humidity) humMin, AVG(p.temperature) temAvg, AVG(p.humidity) humAvg, dm.store_id, DATE_FORMAT(p.collect_date,'%Y-%m-%d') time FROM perception_hcs p, device_manage dm WHERE 1 = 1 AND p.device_id = dm.device_id
AND dm.store_id = #{storeId}
AND date(p.collect_date) = curdate();
