mapper.select:
select
store_id,
code,
name,
building_no,
floor_no,
sort_order,
status,
remark,
creator,
ctime,
modifier,
mtime
from wms_store


		store_id=#{storeId}


		and code=#{code}


		and name=#{name}


		and building_no=#{buildingNo}


		and floor_no=#{floorNo}


		and sort_order=#{sortOrder}


		and status=#{status}


		and remark=#{remark}


		and creator=#{creator}


		and ctime=#{ctime}


		and modifier=#{modifier}


		and mtime=#{mtime}

